# -*- coding: utf-8 -*-
# 获取HTML中的所有专栏链接


from bs4 import BeautifulSoup

def extract_column_link_hrefs(html_string):
    # 使用 BeautifulSoup 解析 HTML 字符串
    soup = BeautifulSoup(html_string, 'html.parser')
    # 查找所有 class 为 ContentItem-head 的标签
    content_item_heads = soup.find_all(class_='ContentItem-head')
    hrefs = []
    # 在每个 ContentItem-head 标签下查找 class 为 TopicLink 的标签
    for head in content_item_heads:
        column_link_tags = head.find_all(class_='TopicLink')
        # 提取这些标签的 href 属性
        hrefs.extend(['https:' + tag.get('href') for tag in column_link_tags if tag.get('href')])
    return hrefs
def save_hrefs_to_txt(hrefs, file_path):
    # 打开文件以写入模式
    with open(file_path, 'w', encoding='utf-8') as file:
        # 将每个 href 属性写入文件，每个 href 占一行
        for href in hrefs:
            file.write(href + '\n')

